import React, { Component } from 'react';

class CreateEmployeeComponent extends Component {
    
    constructor(props){
        super(props)
        this.state = {
            firstName:'',
            lastName:'',
            position:'',
            dateArrival:'',
            status:'',
        }

        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.changePositionHandler = this.changePositionHandler.bind(this);
        this.changeDateArrivalHandler = this.changeDateArrivalHandler.bind(this);
        this.changeStatusHandler = this.changeStatusHandler.bind(this);
    }

    changeFirstNameHandler=(event) => {
        this.setState({firstName: event.target.value})
    }

    changeLastNameHandler=(event) => {
        this.setState({lastName: event.target.value})
    }

    changePositionHandler=(event) => {
        this.setState({position: event.target.value})
    }

    changeDateArrivalHandler=(event) => {
        this.setState({dateArrival: event.target.value})
    }

    changeStatusHandler=(event) => {
        this.setState({status: event.target.value})
    }
    
    render() {
        return (
            <div className='container'>
                <div className='row'>
                    <div className='card col-md-6 offset-md-3 offset-md-3'>
                        <h3 className='text-center'>Employee Form</h3>
                        <div className='card-body'>
                            <form>
                                <div className='form-group'>
                                    <label>First Name:</label>
                                    <input placeholder='First Name' name='firstName' className='form-control'
                                     value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                     <label>Last Name:</label>
                                    <input placeholder='Last Name' name='lastName' className='form-control'
                                     value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                                     <label>Position:</label>
                                    <input placeholder='Position' name='position' className='form-control'
                                     value={this.state.position} onChange={this.changePositionHandler}/>
                                     <label>Date Arrival:</label>
                                    <input placeholder='Date Arrival' name='dateArrival' className='form-control'
                                     value={this.state.dateArrival} onChange={this.changeDateArrivalHandler}/>
                                     <label>Status:</label>
                                    <input placeholder='Status' name='status' className='form-control'
                                     value={this.state.status} onChange={this.changeStatusHandler}/>
                                </div>
                            </form>
                        </div>
                    </div>
                    
                </div>
                
            </div>
        );
    }
}

export default CreateEmployeeComponent;
